import React, { useState, useEffect } from 'react';

const Power = () => {
    const [showMore, setShowMore] = useState(false);
    const [currentLoad, setCurrentLoad] = useState(0);
    const [capacity, setCapacity] = useState(0);
    const [motorData, setMotorData] = useState({ frLoad: 12.5, flLoad: 12.7, brLoad: 12.6, blLoad: 12.5 });
    const [jointData, setJointData] = useState({ j0Load: 2, j1Load: 1.5, j2Load: 1.2, j3Load: 1.3, j4Load: 3, eeLoad: 6 });
    const [labLoadData, setLabLoadData] = useState({ x: 2.2, y: 2.0, z: 1.8, tray: 4.3, gripper: 1 });

    const toggleMenu = () => {
        setShowMore(!showMore);
    };

    useEffect(() => {
      const interval = setInterval(() => {
          // Function to generate random value in the range [-0.4, 0.4] with step 0.1
          const getRandomValue = () => (Math.round((Math.random() * 0.8 - 0.4) / 0.1) * 0.1).toFixed(1);

          setMotorData(prevMotorData => {
              const frLoadCalc = Math.min(4, Math.max(-4, parseFloat(prevMotorData.frLoad) + parseFloat(getRandomValue())));
              const flLoadCalc = Math.min(4, Math.max(-4, parseFloat(prevMotorData.flLoad) + parseFloat(getRandomValue())));
              const brLoadCalc = Math.min(4, Math.max(-4, parseFloat(prevMotorData.brLoad) + parseFloat(getRandomValue())));
              const blLoadCalc = Math.min(4, Math.max(-4, parseFloat(prevMotorData.blLoad) + parseFloat(getRandomValue())));

              return {
                  frLoad: frLoadCalc.toFixed(1),
                  flLoad: flLoadCalc.toFixed(1),
                  brLoad: brLoadCalc.toFixed(1),
                  blLoad: blLoadCalc.toFixed(1)
              };
          });

          setJointData(prevJointData => {
              const j0LoadCalc = Math.min((prevJointData.j0Load+0.4), Math.max((prevJointData.j0Load-0.4), parseFloat(prevJointData.j0Load) + parseFloat(getRandomValue())));
              const j1LoadCalc = Math.min((prevJointData.j1Load+0.4), Math.max((prevJointData.j1Load-0.4), parseFloat(prevJointData.j1Load) + parseFloat(getRandomValue())));
              const j2LoadCalc = Math.min((prevJointData.j2Load+0.4), Math.max((prevJointData.j2Load-0.4)(prevJointData.j0Load-0.4), parseFloat(prevJointData.j2Load) + parseFloat(getRandomValue())));
              const j3LoadCalc = Math.min((prevJointData.j3Load+0.4), Math.max((prevJointData.j3Load-0.4), parseFloat(prevJointData.j3Load) + parseFloat(getRandomValue())));
              const j4LoadCalc = Math.min((prevJointData.j4Load+0.4), Math.max((prevJointData.j4Load-0.4), parseFloat(prevJointData.j4Load) + parseFloat(getRandomValue())));
              const eeLoadCalc = Math.min((prevJointData.j5Load+0.4), Math.max((prevJointData.j5Load-0.4), parseFloat(prevJointData.eeLoad) + parseFloat(getRandomValue())));

              return {
                  j0Load: j0LoadCalc.toFixed(1),
                  j1Load: j1LoadCalc.toFixed(1),
                  j2Load: j2LoadCalc.toFixed(1),
                  j3Load: j3LoadCalc.toFixed(1),
                  j4Load: j4LoadCalc.toFixed(1),
                  eeLoad: eeLoadCalc.toFixed(1)
              };
          });

          setLabLoadData(prevLabLoadData => {
              const xCalc = Math.min(4, Math.max(-4, parseFloat(prevLabLoadData.x) + parseFloat(getRandomValue())));
              const yCalc = Math.min(4, Math.max(-4, parseFloat(prevLabLoadData.y) + parseFloat(getRandomValue())));
              const zCalc = Math.min(4, Math.max(-4, parseFloat(prevLabLoadData.z) + parseFloat(getRandomValue())));
              const trayCalc = Math.min(4, Math.max(-4, parseFloat(prevLabLoadData.tray) + parseFloat(getRandomValue())));
              const gripperCalc = Math.min(4, Math.max(-4, parseFloat(prevLabLoadData.gripper) + parseFloat(getRandomValue())));

              return {
                  x: xCalc.toFixed(1),
                  y: yCalc.toFixed(1),
                  z: zCalc.toFixed(1),
                  tray: trayCalc.toFixed(1),
                  gripper: gripperCalc.toFixed(1)
              };
          });

      }, 1000);

      return () => clearInterval(interval);
  }, []);


    return (
        <div>

            <div style={{ display: 'flex', alignItems: 'center' }}>
                <h5>Power</h5>
                <div onClick={toggleMenu} style={{ cursor: 'pointer', color: '#A9DFBF', transition: 'transform 0.3s' }}>
                    <span style={{ transform: showMore ? 'rotateZ(90deg)' : 'rotateZ(0)', marginLeft: '0.5rem' }}>&#10148;</span>
                </div>
            </div>

            <table className="table table-striped table-bordered table-hover">
                <thead>
                    <tr>
                        <th scope="col" className="col-6" style={{ backgroundColor: '#1C2833', color: '#A9DFBF' }}>Load</th>
                        <th scope="col" className="col-6" style={{ backgroundColor: '#1C2833', color: '#A9DFBF' }}>Capacity</th>
                    </tr>
                </thead>
                <tbody>
                    <tr>
                        <td style={{ backgroundColor: '#1C2833', color: '#A9DFBF' }}>{currentLoad}</td>
                        <td style={{ backgroundColor: '#1C2833', color: '#A9DFBF' }}>{capacity}</td>
                    </tr>
                </tbody>
            </table>

            <div style={{ overflow: 'hidden', transition: 'max-height 0.5s', maxHeight: showMore ? '1000px' : '0' }}>

                Wheels
                <table className="table table-striped table-bordered table-hover">
                    <thead>
                        <tr>
                            <th scope="col" className="col-3" style={{ backgroundColor: '#1C2833', color: '#A9DFBF' }}>FR</th>
                            <th scope="col" className="col-3" style={{ backgroundColor: '#1C2833', color: '#A9DFBF' }}>FL</th>
                            <th scope="col" className="col-3" style={{ backgroundColor: '#1C2833', color: '#A9DFBF' }}>Bl</th>
                            <th scope="col" className="col-3" style={{ backgroundColor: '#1C2833', color: '#A9DFBF' }}>BR</th>
                        </tr>
                    </thead>
                    <tbody>
                        <tr>
                            <td style={{ backgroundColor: '#1C2833', color: '#A9DFBF' }}>{motorData.frLoad}A</td>
                            <td style={{ backgroundColor: '#1C2833', color: '#A9DFBF' }}>{motorData.flLoad}A</td>
                            <td style={{ backgroundColor: '#1C2833', color: '#A9DFBF' }}>{motorData.brLoad}A</td>
                            <td style={{ backgroundColor: '#1C2833', color: '#A9DFBF' }}>{motorData.blLoad}A</td>
                        </tr>
                    </tbody>
                </table>

                Actuators
                <table className="table table-striped table-bordered table-hover">
                    <thead>
                        <tr>
                            <th scope="col" className="col-2" style={{ backgroundColor: '#1C2833', color: '#A9DFBF' }}>J0</th>
                            <th scope="col" className="col-2" style={{ backgroundColor: '#1C2833', color: '#A9DFBF' }}>J1</th>
                            <th scope="col" className="col-2" style={{ backgroundColor: '#1C2833', color: '#A9DFBF' }}>J2</th>
                            <th scope="col" className="col-2" style={{ backgroundColor: '#1C2833', color: '#A9DFBF' }}>J3</th>
                            <th scope="col" className="col-2" style={{ backgroundColor: '#1C2833', color: '#A9DFBF' }}>J4</th>
                            <th scope="col" className="col-2" style={{ backgroundColor: '#1C2833', color: '#A9DFBF' }}>EE</th>
                        </tr>
                    </thead>
                    <tbody>
                        <tr>
                            <td style={{ backgroundColor: '#1C2833', color: '#A9DFBF' }}>{jointData.j0Load}A</td>
                            <td style={{ backgroundColor: '#1C2833', color: '#A9DFBF' }}>{jointData.j1Load}A</td>
                            <td style={{ backgroundColor: '#1C2833', color: '#A9DFBF' }}>{jointData.j2Load}A</td>
                            <td style={{ backgroundColor: '#1C2833', color: '#A9DFBF' }}>{jointData.j3Load}A</td>
                            <td style={{ backgroundColor: '#1C2833', color: '#A9DFBF' }}>{jointData.j4Load}A</td>
                            <td style={{ backgroundColor: '#1C2833', color: '#A9DFBF' }}>{jointData.eeLoad}A</td>
                        </tr>
                    </tbody>
                </table>

                Lab
                <table className="table table-striped table-bordered table-hover">
                    <thead>
                        <tr>
                            <th scope="col" className="col-2.4" style={{ backgroundColor: '#1C2833', color: '#A9DFBF' }}>x</th>
                            <th scope="col" className="col-2.4" style={{ backgroundColor: '#1C2833', color: '#A9DFBF' }}>y</th>
                            <th scope="col" className="col-2.4" style={{ backgroundColor: '#1C2833', color: '#A9DFBF' }}>z</th>
                            <th scope="col" className="col-2.4" style={{ backgroundColor: '#1C2833', color: '#A9DFBF' }}>EM-Tray</th>
                            <th scope="col" className="col-2.4" style={{ backgroundColor: '#1C2833', color: '#A9DFBF' }}>EM-Gripper</th>
                        </tr>
                    </thead>
                    <tbody>
                        <tr>
                            <td style={{ backgroundColor: '#1C2833', color: '#A9DFBF' }}>{labLoadData.x}A</td>
                            <td style={{ backgroundColor: '#1C2833', color: '#A9DFBF' }}>{labLoadData.y}A</td>
                            <td style={{ backgroundColor: '#1C2833', color: '#A9DFBF' }}>{labLoadData.z}A</td>
                            <td style={{ backgroundColor: '#1C2833', color: '#A9DFBF' }}>{labLoadData.tray}A</td>
                            <td style={{ backgroundColor: '#1C2833', color: '#A9DFBF' }}>{labLoadData.gripper}A</td>
                        </tr>
                    </tbody>
                </table>

            </div>

        </div>
    );
};

export default Power;
