import React from 'react';
import { useState , useEffect} from 'react';


const Drivetrain = () => {

    const [gps, setGps] = useState([1.3429032,103.6801850]);
    const [imu, setImu] = useState([0,0,0]);
    const [speed, setSpeed] = useState([0,0]);

    useEffect(() => {
      let increasing = true; // Flag to indicate whether y is increasing or decreasing
    let currentValue = 0.3; // Initial value for y
    let targetValue = 27.2; // Target value for y
    let timeElapsed = 0; // Time elapsed since start

    const interval = setInterval(() => {
        const getRandomValue = () => (Math.round((Math.random() * 0.8 - 0.4) / 0.1) * 0.1).toFixed(1);

        // Increment or decrement y based on the flag
        if (increasing) {
            currentValue += 0.1; // Increment y by 0.1 every second
        } else {
            currentValue -= 0.1; // Decrement y by 0.1 every second
        }

        // Check if y reaches the target value or goes below the initial value
        if (currentValue >= targetValue || currentValue <= 0.3) {
            increasing = !increasing; // Toggle the flag
            timeElapsed = 0; // Reset the time elapsed
        }

        // Update the state with the new values
        setImu(prevImu => {
            const newX = Math.min(0, Math.max(0, parseFloat(prevImu[0]) + parseFloat(getRandomValue())));
            const newY = Math.min(27.2, Math.max(0.3, currentValue)); // Limit y between 0.3 and 27.2
            const newZ = Math.min(0, Math.max(0, parseFloat(prevImu[2]) + parseFloat(getRandomValue())));

            return [newX.toFixed(1), newY.toFixed(1), newZ.toFixed(1)];
        });

        // Increment time elapsed
        timeElapsed += 1;

        // Check if 10 seconds have passed, then reset the target value
        if (timeElapsed >= 10) {
            targetValue = 0.3; // Set the target value back to 0.3
        }


          setSpeed(prevSpeed => {
            const newX = Math.min(0.5, Math.max(0.3, parseFloat(prevSpeed[0]) + parseFloat(getRandomValue())));
            const newY = Math.min(0, Math.max(0, parseFloat(prevSpeed[1]) + parseFloat(getRandomValue())));
            return [newX.toFixed(1), newY.toFixed(1)];
        });


      }, 1000);

      return () => clearInterval(interval);
  }, []);



    return (
      <table className="table table-striped table-bordered">
        <thead>
          <tr>
            <th scope="col" class="col-4" style={{ backgroundColor: '#1C2833', color: '#A9DFBF' }}>GPS</th>
            <th scope="col" class="col-4" style={{ backgroundColor: '#1C2833', color: '#A9DFBF' }}>Inclination</th>
            <th scope="col" class="col-4" style={{ backgroundColor: '#1C2833', color: '#A9DFBF' }}>Speed</th>
          </tr>
        </thead>
        <tbody>
        <tr>
          <td style={{ backgroundColor: '#1C2833', color: '#A9DFBF' }}>{gps[0]},{gps[1]} </td>
          <td style={{ backgroundColor: '#1C2833', color: '#A9DFBF' }}>{imu[0]},{imu[1]},{imu[2]}</td>
          <td style={{ backgroundColor: '#1C2833', color: '#A9DFBF' }}>{speed[0]},{speed[1]}</td>

        </tr>
      </tbody>
      </table>
    );
  };

  export default Drivetrain;
