import React from 'react';
import { useState , useEffect} from 'react';


const Drivetrain = () => {

    const [direction, setDirection] = useState(0);
    const [gps, setGps] = useState([0,0]);
    const [imu, setImu] = useState([0,0,0]);
    const [reference, setReference] = useState([0,0,0]);
    const [speed, setSpeed] = useState([0,0,0]);

    useEffect(() => {
      const interval = setInterval(() => {
        const getRandomValue = () => (Math.round((Math.random() * 0.8 - 0.4) / 0.1) * 0.1).toFixed(1);

          // Update speed state while ensuring it stays in the range of 8 to 12
          setImu(prevImu => {
              let newImu= Math.min(12, Math.max(5, parseFloat(prevImu) + parseFloat(getRandomValue())));
              // Check if newSpeed exceeds the bounds, and if so, set it to the nearest bound
              return newImu.toFixed(1);
          });

      }, 1000);

      return () => clearInterval(interval);
  }, []);


    return (
      <table className="table table-striped table-bordered">
        <thead>
          <tr>
            <th scope="col" class="col-3" style={{ backgroundColor: '#1C2833', color: '#A9DFBF' }}>Direction</th>
            <th scope="col" class="col-2" style={{ backgroundColor: '#1C2833', color: '#A9DFBF' }}>GPS</th>
            <th scope="col" class="col-1" style={{ backgroundColor: '#1C2833', color: '#A9DFBF' }}>Inclination</th>
            <th scope="col" class="col-1" style={{ backgroundColor: '#1C2833', color: '#A9DFBF' }}>Reference</th>
            <th scope="col" class="col-1" style={{ backgroundColor: '#1C2833', color: '#A9DFBF' }}>Speed</th>
          </tr>
        </thead>
        <tbody>
        <tr>
          <td style={{ backgroundColor: '#1C2833', color: '#A9DFBF' }}>{direction}</td>
          <td style={{ backgroundColor: '#1C2833', color: '#A9DFBF' }}>{gps[0]},{gps[1]} </td>
          <td style={{ backgroundColor: '#1C2833', color: '#A9DFBF' }}>{imu[0]},{imu[1]},{imu[2]}</td>
          <td style={{ backgroundColor: '#1C2833', color: '#A9DFBF' }}>{reference[0]},{reference[1]},{reference[2]}</td>
          <td style={{ backgroundColor: '#1C2833', color: '#A9DFBF' }}>{speed[0]},{speed[1]},{speed[2]}</td>

        </tr>
      </tbody>
      </table>
    );
  };

  export default Drivetrain;
